document.addEventListener("DOMContentLoaded",()=>{let t=document.getElementById("star-container");!function(){for(let e=0;e<200;e++){let e=document.createElement("div");e.className="star",e.style.width=`${2*Math.random()+1}px`,e.style.height=e.style.width,e.style.top=`${100*Math.random()}%`,e.style.left=`${100*Math.random()}%`,e.style.animation=`twinkle ${5*Math.random()+5}s infinite alternate`,t.appendChild(e)}}()});